import { Component, OnInit } from '@angular/core';
import { ConversionService } from '../services/conversion.service';

@Component({
  selector: 'app-conversion-form',
  templateUrl: './conversion-form.component.html',
  styleUrls: ['./conversion-form.component.css']
})
export class ConversionFormComponent implements OnInit {
  value: any;
  fromUnit: any;
  toUnit: any;
  result: any;

  constructor(private conversionService: ConversionService) { }

  ngOnInit(): void {
  }

  onSubmit() {
    this.conversionService.convert(this.value, this.fromUnit, this.toUnit)
      .subscribe(result => this.result = result);
  }
}
